import React, { useState, useEffect } from "react";
import axios from "axios";

//User 1 cards
let user1_deck = [];
//User 1 current card drawn
let user1_card = [];

//User 2 cards
let user2_deck = [];
//User 2 current card drawn
let user2_card =[];

function WarGame() {

  const setShuffled = useState("");
  const shuffled = useState("");
  const updateRender = useState();
  let currentDeck

  //Calls new game when the page loads
  useEffect(() => {
    newGame();
  }, []);

  function newGame(){

    user1_deck = [];
    user1_card = [];
    user2_deck = [];
    user2_card = [];
    //Make API call to retrieve cards for game
    axios.get(`https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1`)
      .then(result => {
        result.data.success && console.log("Deck obtained");

        //Shuffle deck
        setShuffled(result.data.shuffled);
        //Retrieve deck
        currentDeck = result.data.deck_id;

        //Clear decks in both users hands
        user1_deck = [];
        user2_deck = [];

        //Make API request to evenly split cards between both users
        axios.get(`https://deckofcardsapi.com/api/deck/${currentDeck}/draw/?count=52`)
          .then(result => {
            result.data.success && console.log("Deck is split!")

            let i;
            for (i = 0; i < 52; i += 2){
              let card1 = result.data.cards[i];

              //Card object to display user 1's hand
              let drawnCard1 = {
                value: `${card1.value}`,
                suit: `${card1.suit}`,
                imageURL: `${card1.image}`,
                cardCode: `${card1.code}`,
                imageURL: `${card1.image}`
              };

              let card2 = result.data.cards[i + 1];
              //Card object to display user 2's hand
              let drawnCard2 = {
                value: `${card2.value}`,
                suit: `${card2.suit}`,
                imageURL: `${card2.image}`,
                cardCode: `${card2.code}`,
                imageURL: `${card2.image}`
              };

              user1_deck.push(drawnCard1);
              user2_deck.push(drawnCard2);
            }
            //Pushes new card to the browser
            updateRender(n => !n);
          })
          .catch(error => console.log(error));
      })
    .catch(error => console.log(error));
  }

  function deal() {
    try {
      //reset the pile for each player
      user1_deck = [];
      user2_deck = [];
      if (!gameWon()) {
        let player1 = user1_deck.pop();
        let player2 = user2_deck.pop();
        if (!(player1 === "undefined" && player2 === "undefined")) {
          player_card.push(player1);
          dealer_card.push(player2);
          updateRender(n => !n);
          computeFlipWinner();
        }
      } else {
        gameWon();
        updateRender(n => !n);
      }
    } catch (e) {}
  }

  //computes who won the flip
  async function drawWinner() {
    //get player card value
    let user1Score = getCardValue(user1_card[player_card.length - 1]);
    //get deal card value
    let user2Score = getCardValue(dealer_card[dealer_card.length - 1]);

    if (player_score === dealer_score) {
      //its a tie
      console.log("Tie");
      drawTie();
    } else if (player_score > dealer_score) {
      //player wins the flip
      console.log("Player wins this round");
      while (player_card.length > 0) {
        await sleepDelay(1);
        addCard(player_pile, player_card.pop());
        addCard(player_pile, dealer_card.pop());
      }
    } else {
      //dealer wins
      console.log("Dealer wins this round");
      while (player_card.length > 0) {
        await sleepDelay(1);
        addCard(dealer_pile, player_card.pop());
        addCard(dealer_pile, dealer_card.pop());
      }
    }
  }



  return (
    <>
    <div>
      <div>
        <div>
          <button onClick={draw}>Hit</button>
          <button onClick={startNewGame}>
            New Game
          </button>
        </div>
      </div>
      <div>
        <div type="player">
          <h2>Player Card</h2>
          {player_card &&
            player_card.map((card, index) => {
              return (
                <img
                  key={`player${index}`}
                  src={`${card.imageURL}`}
                  alt={`${card.value} of ${card.suit}`}
                />
              );
            })}
        </div>
        <div>
          <h2>Dealer Card</h2>
          {dealer_card &&
            dealer_card.map((card, index) => {
              return (
                <img
                  key={`dealer${index}`}
                  src={`${card.imageURL}`}
                  alt={`${card.value} of ${card.suit}`}
                />
              );
            })}
        </div>
      </div>

      <div>
        <p>Player cards: {player_pile.length}</p>
        <p>Dealer cards: {dealer_pile.length}</p>
        <p>{/*winner*/}</p>
      </div>
    </div>
  </>
  )

}

export default WarGame;
