import React, { useState, useEffect } from "react";
import axios from "axios";

//User 1 cards
let user1_deck = [];
//User 1 current card drawn
let user1_card = [];

//User 2 cards
let user2_deck = [];
//User 2 current card drawn
let user2_card =[];

function WarGame() {

  const setShuffled = useState("");
  const shuffled = useState("");
  let currentDeck

  //Calls new game when the page loads
  useEffect(() => {
    newGame();
  }, []);

  function newGame(){

    user1_deck = [];
    user1_card = [];
    user2_deck = [];
    user2_card = [];
    //Make API call to retrieve cards for game
    axios.get(`https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1`)
      .then(result => {
        result.data.success && console.log("Deck obtained");

        setShuffled(result.data.shuffled);
        currentDeck = result.data.deck_id;

        //Clear the cards
      })

  }

}
