import React, { useState, useEffect } from "react";
import axios from "axios";

let player_pile = []; //cards the player has
let player_card = []; //card the player drew

let dealer_pile = []; //cards the dealer has
let dealer_card = []; //card the dealer drew

function WarGame() {
  const [shuffled, setShuffled] = useState("");
  const [, updateRender] = useState();
  let curDeck;
  void shuffled;

  //calls startNewGame() when th page loads
  useEffect(() => {
    startNewGame();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // Create a new deck of cards and store the deck id for future API calls
  function startNewGame() {
    player_pile = [];
    dealer_pile = [];
    player_card = [];
    dealer_card = [];
    axios
      .get(`https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1`)
      .then(result => {
        result.data.success && console.log("Deck obtained");

        setShuffled(result.data.shuffled);
        curDeck = result.data.deck_id;
        //clear the cards in the player and dealer hands
        player_pile = [];
        dealer_pile = [];
        //split the deck
        axios
          .get(`https://deckofcardsapi.com/api/deck/${curDeck}/draw/?count=52`)
          .then(result => {
            result.data.success && console.log("Beginning split");

            let i;
            for (i = 0; i < 52; i += 2) {
              let card1 = result.data.cards[i];
              //create a card object to push onto the player hand
              let drawn_card1 = {
                value: `${card1.value}`,
                suit: `${card1.suit}`,
                imageURL: `${card1.image}`,
                cardCode: `${card1.code}`,
                faceUp: `${card1.image}`
              };

              let card2 = result.data.cards[i + 1];
              //create a card object to push onto the dealer hand
              let drawn_card2 = {
                value: `${card2.value}`,
                suit: `${card2.suit}`,
                imageURL: `${card2.image}`,
                cardCode: `${card2.code}`,
                faceUp: `${card2.image}`
              };

              //add 1 card to both player and dealer piles
              player_pile.push(drawn_card1);
              dealer_pile.push(drawn_card2);
            }
            updateRender(n => !n);
          })
          .catch(error => console.log(error));
      })
      .catch(error => console.log(error));
  }

  function draw() {
    try {
      //reset the pile for each player
      dealer_card = [];
      player_card = [];
      if (!gameWon()) {
        let cp = player_pile.pop();
        let cd = dealer_pile.pop();
        if (!(cp === "undefined" && cd === "undefined")) {
          player_card.push(cp);
          dealer_card.push(cd);
          updateRender(n => !n);
          computeFlipWinner();
        }
      } else {
        gameWon();
        updateRender(n => !n);
      }
    } catch (e) {}
  }

  async function drawTie() {
    let i;
    for (
      i = 0;
      i < 3;
      i++ //flip 3 cards face down
    ) {
      player_card.push(player_pile.pop());

      dealer_card.push(dealer_pile.pop());
    }
    player_card.push(player_pile.pop());
    dealer_card.push(dealer_pile.pop());
    await sleepDelay(1);
    updateRender(n => !n);
    computeFlipWinner();
  }

   //Scores for whoever wins round
   async function drawWinner() {
    //get player card value
    let user1Score = cardValue(user1_card[user1_card.length - 1]);
    //get deal card value
    let user2Score = cardValue(user2_card[user2_card.length - 1]);

    if (user1Score === user2Score) {
      //its a tie
      alert('Tie')
    } else if (user1Score > user2Score) {
      //player wins the flip
      alert("User1 wins this round");
    } else {
      //dealer wins
      alert("User2 wins this round");
    }
  }

  //Gets te value of a given card
  function getCardValue(card) {
    try {
      switch (card.value) {
        case "ACE": //highest value card in the game
          return 14;
        case "KING":
          return 13;
        case "QUEEN":
          return 12;
        case "JACK":
          return 11;
        default:
          return card.value;
      }
    } catch (e) {}
  }

  //Adds the given card to the given pile
  function addCard(pile, card) {
    let i;
    try {
      card.imageURL = card.faceUp;
      for (i = pile.length; i > 0; i--) {
        pile[i] = pile[i - 1];
      }
      pile[0] = card;
    } catch (e) {}
  }

  //checks if either player has an empty deck
  function gameWon() {
    console.log("P: " + player_pile.length + " D: " + dealer_pile.length);
    if (player_pile.length === 0) {
      return true;
    }
    if (dealer_pile.length === 0) {
      return true;
    }
    return false;
  }

  function sleepDelay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
  }

  return (
    <>
      <div>
        <div>
          <div>
            <button onClick={draw}>Hit</button>
            <button onClick={startNewGame}>
              New Game
            </button>
          </div>
        </div>
        <div>
          <div type="player">
            <h2>Player Card</h2>
            {player_card &&
              player_card.map((card, index) => {
                return (
                  <img
                    key={`player${index}`}
                    src={`${card.imageURL}`}
                    alt={`${card.value} of ${card.suit}`}
                  />
                );
              })}
          </div>
          <div>
            <h2>Dealer Card</h2>
            {dealer_card &&
              dealer_card.map((card, index) => {
                return (
                  <img
                    key={`dealer${index}`}
                    src={`${card.imageURL}`}
                    alt={`${card.value} of ${card.suit}`}
                  />
                );
              })}
          </div>
        </div>

        <div>
          <p>Player cards: {player_pile.length}</p>
          <p>Dealer cards: {dealer_pile.length}</p>
          <p>{/*winner*/}</p>
        </div>
      </div>
    </>
  );
}

export default WarGame;
